var Toon = require('./../node-toon');

// Create new toon instance with key and secret
var toon = new Toon('<client_id>', '<client_secret>');

// Authorize the toon instance with a username and password
toon.authorize('<username>', '<password>', function () {

	// Double check if authorized
	if (toon.authorized()) {

		// Get the target temperature
		toon.getTargetTemperature(function (err, temperature) {
			console.log('Current target temperature is ' + temperature + ' degrees celsius');
		});

		// Get display status
		toon.getStatus(function (err, result) {
			console.log(result);
		});
	}
});
